INSERT INTO public.products (id, sku, type, name, is_published, short_description, description, in_stock, stock, categories, images, price) VALUES (2001, '24-MG04', 'simple', 'Aim Analog Watch', true, 'This is a simple product called Aim Analog Watch', '<p>Stay light-years ahead of the competition with our Aim Analog Watch. The flexible, rubberized strap is contoured to conform to the shape of your wrist for a comfortable all-day fit. The face features three illuminated hands, a digital read-out of the current time, and stopwatch functions.</p> <ul> <li>Japanese quartz movement.</li> <li>Strap fits 7" to 8.0".</li> </ul>', true, 100, 'Gear>Watches', 'http://eimages.valtim.com/acme-images/product/m/g/mg04-bk-0.jpg', 45);
INSERT INTO public.products (id, sku, type, name, is_published, short_description, description, in_stock, stock, categories, images, price) VALUES (2002, '24-MG01', 'simple', 'Endurance Watch', true, 'This is a simple product called Endurance Watch', '<p>It''s easy to track and monitor your training progress with the Endurance Watch. You''ll see standard info like time, date and day of the week, but it also functions for the serious high-mileage athete: lap counter, stopwatch, distance, heart rate, speed/pace, cadence and altitude.</p> <ul> <li>Digital display.</li> <li>LED backlight.</li> <li>Strap fits 7" to 10".</li> <li>1-year limited warranty.</li> <li>Comes with polished metal case.</li> </ul>', true, 100, 'Gear>Watches', 'http://eimages.valtim.com/acme-images/product/m/g/mg01-bk-0.jpg', 49);
